function copyCode(){document.querySelectorAll("pre > code[data-lang]").forEach(function(b){const a=document.createElement("button");a.className="copy-code-button",a.type="button",a.innerText="Copy",a.addEventListener("click",function(c){c.preventDefault(),navigator.clipboard.writeText(b.innerText).then(function(){a.blur(),a.innerText="Copied",setTimeout(function(){a.innerText="Copy"},1500)}).catch(function(a){console.error(`${a} Error copying code.`)})}),b.closest(".highlight").insertAdjacentElement("afterbegin",a)})}copyCode()